u $(cat ~/.ssh/jira_creds)  "https://uk-gov-dft.atlassian.net/rest/api/2/search?maxResults=5000" | jq '.issues[] | {id: .id, key: .key, data: .fields | {resolved:.resolutiondate,created:.created, type: .issuetype.name, status: .status.name, updated: .updated, creator: .creator.name, reporter: .reporter.name}} | [.id,.key,.data.resolutiondate,.data.created,.data.type,.data.status,.data.updated,.data.creator,.data.reporter] | @csv' >> ~/jira_issues.csv
